    const img = document.createElement("img"); // ||  const img = new Image();
    img.src = "https://gimg2.baidu.com/image_search/src=http%3A%2F%2Fpic1.win4000.com%2Fwallpaper%2F2020-04-07%2F5e8c1c1f397b9.jpg&refer=http%3A%2F%2Fpic1.win4000.com&app=2002&size=f9999,10000&q=a80&n=0&g=0n&fmt=jpeg?sec=1641609272&t=8166b79fc50ddaf648982d4171ad040f";

    // 错误方式一：直接输出
    // console.log(`width: ${img.width}; height: ${img.height}`); 
    // 输出结果为：width: 0; height: 0
    // 说明：图片未被加载，获取的 宽、高 都是0


    // 正确方式一：在图片所有相关数据被加载后触发
    /* img.onload = function() {
        console.log(`from onload, width: ${img.width}; height: ${img.height}`);
    } */
    // 输出结果为：from onload, width: 1920; height: 1080
    // 说明：onload在图片相关数据被加载后触发

    // 正确方式二：我们知道有些图片虽然没有完全down下来，但是已经先有了占位符，然后一点一点的加载。既然有占位符那应该是请求图片资源服务器响应后返回的。问题是 服务器什么时候响应并返回宽高的数据呢，使用一个 间歇定时器来循环判断 图片宽高是否已经能读取的到
    /* const check = function() {
        if (img.width || img.height) {
            console.log(`from check, width: ${img.width}; height: ${img.height}`);
            clearInterval(interval);
        }
    }
    const interval = setInterval(check, 10); */

    // 对比正确方式一 和 方式二 两者的用时
    const startDate = new Date().getTime();
    const check = function() {
        if (img.width || img.height) {
            const diff = new Date().getTime() - startDate;
            console.log(`from check, width: ${img.width}; height: ${img.height}, 用时${diff}ms`);
            clearInterval(interval);
        }
    }
    const interval = setInterval(check, 10);

    img.onload = function() {
        const diff = new Date().getTime() - startDate;
        console.log(`from onload, width: ${img.width}; height: ${img.height}, 用时${diff}ms`);
    }
    // from check, width: 1920; height: 1080, 用时90ms
    // from onload, width: 1920; height: 1080, 用时353ms（每次请求事件略有出入，不必计较）

