function getSearchParams() {
  const { search } = window.location;
  if (!search) return '';
  return search
    .slice(1)
    .split('&')
    .map(group => group.split('='))
    .reduce((searchParamsObj, item) => {
      searchParamsObj[item[0]] = item[1];
      return searchParamsObj;
    }, {});
}

