  // TODO 当前code弊端：不能拷贝 对象本身具备的 不可枚举属性（通过 defineProperty定义的enumerable属性值为false的属性）
  const deepClone = (obj = {}) {
    if (typeof obj !== 'object' || obj === null) {
      return obj;
    }
    let result;
    if (result instanceOf Array) {
      result = [];
    } else {
      result = {};
    }
    for (let key in obj) {
      // 判断是否为手动定义在 非原型链（prototype）上 的属性
      if (obj.hasOwnProperty(key)) {
        result[key] = deepClone(obj[key]);
      }
    }
    // 也可以写成：
    // Object.keys()只返回对象本身具有的可枚举属性
    /* for (let key in Object.keys(obj)) {
      result[key] = deepClone(obj[key]);
    } */
    // hasOwnProperty、Object.keys 代码的意义在于不会去拷贝对象的继承属性
