  function isEqual (obj1, obj2) {
    // 简单数据类型直接进行全等比较
    if (!isObject(obj1) || !isObject(obj2)) {
      return obj1 === obj2;
    }
    // 引用类型指针指向的地址相同 || 值类型全等
    if (obj1 === obj2) {
      retrun true;
    }
    // 根节点对象自身 属性数量如果不相等，直接返回false
    if (Object.keys(obj1).length !== Object.keys(obj2).length) {
      return false;
    }
    // 枚举每个属性值 逐个进行比较
